(1271, 142, 'blocks_0_image_image', '119'),
(1272, 142, '_blocks_0_image_image', 'field_6358383464a07'),
(1273, 142, 'blocks_0_image', ''),
(1274, 142, '_blocks_0_image', 'field_6358380764a06'),
(1275, 142, 'blocks_1_type', 'text'),
